sp_widget.client_script:
function() {
  /* widget controller */

  var c = this;
	console.log(c.data);
	c.board_x = -12;
	c.board_y = 12;
	c.board = [];
	for(var y = 10; y >= -10; y--){
		for(var x=-10; x<=10; x++){
			var obj = {};
			obj.x = x;
			obj.y = y;
			c.board.push(obj);
		}
	}
}

